site.headStyle:
@import url(https://fonts.googleapis.com/css?family=Montserrat:400,400i,700,700i&display=swap);

.all-style .silex-element-content>* {
  font-family: 'Montserrat', sans-serif;
}

.style-menu li {
  display: inline-block;
}

.style-menu :nth-child(1n) a {
  color: #8055BD;
}

.style-menu :nth-child(2n) a {
  color: #5B6EC6;
}

.style-menu :nth-child(3n) a {
  color: #3CABA9;
}

.style-menu :nth-child(4n) a {
  color: #FFAE00;
}

.style-menu :nth-child(5n) a {
  color: #F4B430;
}

.style-menu :nth-child(6n) a {
  color: #7F7F7F;
}

.body-initial {
  background: linear-gradient(90deg, rgba(237, 236, 245, 1) 0%, rgba(255, 255, 255, 1) 100%);
}

.menu-bandeau {
  background: linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) 5%, rgba(255, 255, 255, 1) 95%, rgba(255, 255, 255, 0) 100%);
}

.style-footer u,
.style-top-bar u {
  border-radius: 30px;
}

.active-menu {
  border-bottom: 2px solid #fff !important;
}

a.active-menu {
  border-color: inherit !important;
}

.fixed {
  z-index: 20 !important;
}

@media (max-width:480px) {
  .menu-bandeau {
    transform: none !important;
  }
}
.style-deg-rouge {
  background: linear-gradient(90deg, #C22C25, #FE705F)!important;
}
.style-deg-purple {
  background: linear-gradient(90deg, #8055BD, #AC53CA)!important;
}
.style-deg-bleu {
  background: linear-gradient(90deg, #5B6EC6, #03AADA)!important;
}

.style-deg-orange-titres, .style-deg-green-titres {
    background: initial!important;
}
